import React from 'react';
import { Link } from 'react-router-dom';
import { useRef, useState } from 'react'

const initialState = {
    "id": "",
    "customer_name": " ",
    "customer_email": "",
    "product": "",
    "quantity": ''
  }

const Navbar = () => {

    const ref = useRef(null)
    const refClose = useRef(null)
    const [order, setorder] = useState(initialState);
    const { customer_name, customer_email, product, quantity } = order;

    const onChange = (e) => {

        setorder({ ...order, [e.target.name]: e.target.value })




        return (
            <div className="fixed">
                <nav className="navbar navbar-expand-lg navbar-light bg-light">
                    <div className="container-fluid">
                        <Link className="navbar-brand" to="/"><h3>Utilize</h3></Link>
                        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                            <span className="navbar-toggler-icon"></span>
                        </button>
                        <div className="collapse navbar-collapse" id="navbarSupportedContent">
                            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                                <li className="nav-item">
                                    <Link className="nav-link active" aria-current="page" to="/">Home</Link>
                                </li>
                                <li className="nav-item">
                                    <Link className="nav-link" to="/allOrders">Your Orders</Link>
                                </li>
                                <li className="nav-item">
                                    <Link className="nav-link" to="/addNewOrder">Add New Order</Link>
                                </li>
                            </ul>
                            
                        </div>
                    </div>
                </nav>
            </div>
        )
    }
}

  export default Navbar
